INSERT INTO parks (name, content, latitude, longitude) VALUES
('Kungsträdgården', 'Kungsträdgården är Stockholms mest centrala park och har sina historiska rötter i 1400-talet. Parken är en av stadens allra viktigaste mötesplatser, och det centrala läget gör den till en grön mittpunkt i city.  
', 59.33084441003073, 18.07186928509849),
('Berzelii Park', 'Berzelii park ligger nära Nybroviken. Det är en traditionell stadspark med välskötta gräsytor, blomsterarrangemang och gångstråk – en oas mitt i staden och en plats där du kan slå dig ner och vila en stund.', 59.332505340550725, 18.07468856068874),
('Årstafältet', 'Under de närmaste åren kommer en del av Årstafältet att bli bostadsområde och en annan del blir stadspark.', 59.29018846452646, 18.045535688031144),
('Strömparterren', 'Som en smaragd, ett smycke och lite som en överraskning ligger Strömparterren längst ut på Helgeandsholmen, mitt i Strömmen mellan Stockholms slott och Kungliga Operan. ', 59.32838332844039, 18.070550222947578),
('Norra Bantorget', 'Norra Bantorget är en historisk och kulturell plats med viktiga minnesmärken. Parken ligger på Norrmalm mitt i centrala Stockholm, nära LO-borgen och Norra Latin.', 59.33494532327294, 18.054137334374037),
('Järvafältet', 'Kulturreservatet Järvafältet erbjuder besökare fina naturupplevelser med både fält och skogar – långt bort från stadens brus.', 59.42890933740005, 17.894618784397437),
('Nobelparken', 'I slutet av Strandvägen på gränsen till Diplomatstaden ligger Nobelparken. Här finns både en vackert anlagd strandpromenad och naturlika delar med många olika arter av barr- och lövträd.', 59.3320419978746, 18.098402251924245),
('Bellevueparken', 'Bellevueparken ligger i Norrtull bakom Wenner-Gren Center. Parken erbjuder både vattenkontakt och vacker utsikt över staden och Brunnsviken. ', 59.3539546873997, 18.048062915994283),
('Hornsbergs strandpark', 'Hornsbergs strandpark är en relativt ny park som sträcker sig längs Ulvsundasjön i den nya delen av Nordvästra Kungsholmen. På sommaren är parken mycket välbesökt och vibrerar då av stadsliv. 
', 59.340500746327336, 18.006460609919532),
('Rålambshovsparken', 'Rålambshovsparken, som också kallas Rålis, är en av Stockholms mest populära parker där människor i alla åldrar möts för lek, motion och avkoppling.', 59.32852798544911, 18.02459239497784),
('Norr Mälarstrand', 'Norr Mälarstrand är det internationellt kända parkstråk som löper utmed Kungsholmens strandlinje mellan Rålambshovsparken i väster och Kungsholms torg i öster.', 59.32756661890891, 18.034414175033493),
('Kronobergsparken', 'Kronobergsparken ligger i änden av Drottningholmsvägen och nära Fridhemsplan. Parken är välbesökt och används för lek, promenader, picknick och för en stunds avkoppling.', 59.33213835460433, 18.035093401171473),
('Kristinebergs slottspark', 'Denna park ligger i anslutning till Kristinebergs slott och har anor från 1600-talet. Parken har förnyats i samband med stadsutvecklingen på Nordvästra Kungsholmen. I parken finns en mycket omtyckt lekplats.', 59.333853359295865, 18.0070390426567),
('Fredhällsparken', 'Fredhällsparken ligger på Västra Kungsholmen utmed Drottningholmsvägen. Parken är uppskattad för sina öppna gräsytor och för den stora plaskdammen. ', 59.3312796371313, 18.007436418729895),
('Luxparken', 'Luxparken ligger på Lilla Essingen och är en del av strandpromenaden längs öns östra del. Här får du som besökare fina utblickar över Mälaren och Långholmen.', 59.324277729788086, 18.009836510263796),
('Centralbadets gård', 'Centralbadets gård är en lugn oas mitt i stadens brus. Parken är lummig och innehåller en vacker damm med en vattenskulptur. ', 59.33638955841224, 18.059794300222745),
('Tegnérlunden', 'Tegnérlunden ligger i Vasastaden. Parken är kanske mest känd för Carl Eldhs pampiga staty av August Strindberg och för att den figurerar i flera av Astrid Lindgrens böcker. ', 59.33815535212221, 18.054504146171297),
('Observatorielunden', 'Observatorielunden ligger utmed Sveavägen och sträcker sig upp mot Observatoriekullen. I parken finns Sveriges första observatorium och Stadsbiblioteket ritat av arkitekt Gunnar Asplund.', 59.341762199646965, 18.05478162516333),
('Spökparken', 'Spökparken ligger i Vasastaden och har sina huvudentréer från Drottninggatan och Holländargatan. Parken är omtyckt för sin lummighet och spännande historia. I parken finns en malmgård och ett lusthus från 1700-talet.', 59.340230127853424, 18.056246573470254),
('Enkehusparken', 'Enkehusparken ligger i Vasastaden och har sin huvudentré från Norrtullsgatan. Parken är en uppskattad mötesplats i kvarteret och den används flitigt av intilliggande skolor och förskolor.', 59.34562618410067, 18.046755237878873),
('Vanadislunden', 'Vanadislunden ligger i stadsdelen Vasastaden. Parken sträcker sig upp på en höjd där du som besökare har fin utsikt över stadens takåsar. ', 59.34848476815964, 18.054010232570825),
('Vasaparken', 'Vasaparken ligger centralt i Vasastaden. Parken är, och har länge varit, en viktig mötesplats för alla åldrar. Parkleken, bollplanen och skridskobanan är mycket uppskattade och välbesökta.', 59.34007400081019, 18.042888758560768),
('Monica Zetterlunds park', 'Monica Zetterlunds park ligger i korsningen av Roslagsgatan och Surbrunnsgatan. Den är en grön oas i ett område med tät kvartersbebyggelse. Parken är känd för sin unika parksoffa som spelar låtar framförda av Monica Zetterlund. ', 59.34637012062544, 18.060530061210212),
('Humlegården', 'Humlegården ligger ett stenkast från Stureplan och är en av Stockholms äldsta parker. Parken är en grön oas för människor i alla åldrar och här finns plats för lek, promenader, picknick eller en stunds avkoppling.', 59.339861482207475, 18.072983588059238),
('Ellen Keys park', 'Ellen Keys park är en grön oas med fina planteringar och sittplatser. Här finns också en sagolik lekplats inspirerad av Ellen Key. Parken har sin huvudentré från Birger Jarlsgatan vid Jarlaplan. ', 59.343861607706955, 18.06429167095884),
('Tessinparken', 'Tessinparken ligger på Gärdet och har sin huvudentré från Valhallavägen. Sommartid är parkens särskilt vacker med sina rosplanteringar och sprudlande fontän.', 59.342684122626494, 18.094677548864162),
('Gustav Adolfsparken', 'Gustav Adolfsparken ligger i slutet av Karlavägen, mitt emot kvarteret Garnisonen. Parken är en viktig mötesplats för alla åldrar och används av många skolor och förskolor i området. ', 59.33696481632165, 18.097549979534254),
('Husarviksparken', 'Husarviksparken är en av flera nya parker i den nya stadsdelen Norra Djurgårdsstaden. Parken ligger intill Husarvikens vatten och Norra Djurgårdens naturmarker. ', 59.357428025285614, 18.08630700213677),
('Eriksbergsparken', 'Eriksbergsparken är en högt belägen park som trots närheten till den trafikerade Birger Jarlsgatan ändå upplevs som rofylld. Parken är uppskattad som lunch- och picknickplats.', 59.34135818536067, 18.06672790769687),
('Sinnenas trädgård', 'En rofylld, grön oas mitt i storstaden. Dofterna, färgerna och det porlande vattnet och gruset som knastrar under fötterna i den uppvuxna trädgården. Välkommen in att besöka Sinnenas trädgård. ', 59.339120053598975, 18.04279282875535),
('Långbroparken', 'Långbroparken är en vacker grön oas som anlades i början av 1900-talet som en sjukhuspark. Den har ett varierat folkliv och används till lek, picknick, löpträning och promenader. Parken ligger på gångavstånd från Fruängens centrum, med huvudentré från Mickelsbergsvägen.', 59.282630345718225, 17.971877411061005),
('Örby slottspark', 'Örby slottspark är en historisk parkanläggning som fungerar som en grön oas för såväl lek som vila. Där finns stora gräsytor, grusgångar, häckar, planteringar, en fontändamm och en plaskdamm. Parken ligger bakom slottet på Örby slottsväg. ', 59.281402993658766, 18.029753628625606),
('Prästgårdsparken', 'Prästgårdsparken i Solberga består dels av den gamla fina sjukhusparken med planteringar och fontän, dels av en lekplats med bollplaner. Parken ligger på gångavstånd från Älvsjö station på vägen mot Solberga.
Prästgårdsparken är just nu avstängd på grund av upprustning. Vi räknar med att parken öppnar till sommaren 2022.', 59.28172913672013, 18.008972437603184),
('Lerkrogsparken', 'Centralt i Älvsjö centrum finns en nyligen upprustad park för vila och promenader. Lerkrogsparken passar bra för att ta en paus och insupa lite grönska. Den nås från Götalandsvägen eller Spikskogatan.', 59.27970638252437, 18.007584394001768),
('Solbergaskogen', 'Solbergaskogen är en liten gammelskog med riktig vildmarkskänsla. Här möter du en artrik naturskog med krokiga tallar, kraftiga bärris och små vitmossekärr. Genom skogen löper ett nät av breda gång- och cykelvägar med belysning. Här finns också bland annat hundrastgård, utegym och parkleken Kristallen.', 59.28543267258213, 18.001622716462755),
('Lumaparken', 'Lumaparken i Hammarby sjöstad är en stadsdelspark som stoltserar med vackra japanska körsbärsträd. ', 59.30465377459535, 18.097855760009725),
('', '', 59.31119762624421, 18.10491823115878),
('Nytorget', 'Nytorget har formen av en rektangel och är delat i en övre och nedre terrass. Parken har en stor konstgräsyta med en prydnadsdamm och stora sommarblomsplanteringar och i södra delen finns en stor grusad lekplats för både små och stora barn.', 59.312200076906066, 18.08269381907305),
('Stora Blecktornsparken', 'Stora Blecktornsparkens fria öppna gräsytor gör den populär för spontanidrott och bollek. I parkens nordvästra del ligger en parklek med öppen förskola med djurhagar för parklekens djur.', 59.307156614234955, 18.086825706965058),
('Lilla Blecktornsparken', 'Lilla Blecktornsparken ligger nedsänkt i förhållande till omgivande gator och ramas in av stora alléträd. Här hittar du utegym, tennis- och bollplan i sydväst och intill Katarina Bangata ligger en stjärnformad plaskdamm.', 59.30946585672466, 18.086001840751614),
('Vitabergsparken', 'Vitabergsparken är en kuperad naturpark med många träd och stora gräsytor som gör den till en populär picknickpark. Berg och kullar ger parken många rum och undangömda platser. I parken finns friluftsteater, hundrastgård, musikpaviljong, koloniområde och lekplats.', 59.31237401961087, 18.088212554442354),
('Björns trädgård', 'Björns trädgård i anslutning till Medborgarplatsen är en välbesökt park som ramas in av höga kastanjer. I parken finns en välbesökt parklek med lekutrustning, lekytor och en populär skateboardramp.', 59.31573990030905, 18.075018912352906),
('Årsta holmar', 'Årsta holmar upplever du från Eriksdalslunden vid Årstavikens strandpromenad där holmarna ligger som blickfång mitt i Årstaviken. Årsta holmar utgörs av tre holmar. Alholmen i väster, Bergsholmen i mitten och Lillholmen i öster.', 59.30829342190516, 18.042131532188),
('', '', 59.323377789549504, 18.0723605319596),
('Rosenlundsparken', 'I Rosenlundsparkens östra del dominerar den välbesökta parkleken Skånegläntan. Närmast Västgötagatan ligger ”Stranden”, en sittplats med gula solstolar och en vattenlek. ', 59.311250947721575, 18.069452101659422),
('Mosebacke Torg', 'Mosebacke Torg är en livfull plats bara ett kvarter från Götgatan. Grusade gångstigar gör torget tillgängligt från flera olika håll. I en grusad rundel omgärdad av klippta häckar finns sommartid ett litet café.', 59.31803511430289, 18.074213246181802),
('Eriksdalslunden-Årstalunden', 'Mellan Skanstullsbron och Årstabron finns ett cirka två kilometer långt strandområde av naturkaraktär. Längs stranden finns flera båtklubbar som ligger vid bryggor sommartid och upplagsområden vintertid. Intill strandpromenaden finns flera koloniträdgårdsområden. ', 59.305098112179095, 18.081904954824452),
('Tantolunden', 'Tantolunden är en lummig bergspark som domineras av stora träd och mycket vegetation, breda och generösa gångvägar. Parkens är en av Södermalms mest välbesökta parker och fungerar som en viktig länk mellan Ringvägen och Årstaviken. ', 59.313637371098324, 18.04958620878268),
('Skinnarviksparken', 'I Skinnarviksparken vid Skinnarviksberget hittar du många vackra utsiktsplatser. Här finns Bergets krön, Stockholms högsta naturliga punkt som ligger 53 meter över havet. Lekplats, gräsytor, berghällar, sittplatser och många olika sorters träd.', 59.320069553470375, 18.048206035768306),
('Mariatorget', 'Mariatorget är en populär och livfull stadspark som kantas av stora lindar i alléerna där boulespel förekommer. Gångvägarna är av grus med lister av smågatsten. Det finns en lekplats, kiosk och en stadstoalett och i parken.', 59.31820978315825, 18.06074716897717),
('Långholmen', 'Långholmen är en grön och lummig naturpark med vildvuxen grönska. Parken är en välbesökt promenadpark. Den vackra kulturmiljöbebyggelsen i parken, utblickarna över vattnet och de många fantastiska träden bidrar till Långholmens särpräglade karaktär.', 59.32039180659025, 18.024622703867195),
('Vårbergstoppen', 'I Vårberg finns en park som lockar både barn och vuxna i alla åldrar. Här finns spännande lekmiljöer, magnifika utsiktspunkter, fågelskådningsplatser, utegym samt utflyktsplatser med picknickbord och grillar. Allt detta med respekt för parkens natur- och kulturvärden. ', 59.27287005004575, 17.879351214365244),
('Sätradalsparken', 'Sätradalsparken är en omtyckt stadsdelspark. Parken ligger centralt, nära Sätra centrum, och är en av Skärholmen stadsdelsområdes största parker.', 59.285378742717924, 17.91597878769377),
('Magelungens strandpromenad', 'Längs sjön Magelungens norra strand finns en lång strandpromenad. Ett fem kilometer långt stråk beläget mellan Fagersjö och Farsta strand.', 59.23405294054221, 18.087926052597542),
('Drevvikens strandpromenad', 'Det är enkelt att ta sig fram kring den natursköna Drevviken. Här promenerar du i kulturhistoriska miljöer med gott om möjligheter för paus och solbadande.', 59.246232728226985, 18.109602752506117),
('Olympiaplan', 'I  östra delen av Olympia-området finns den populära parken Olympiaplan. ', 59.270357489046816, 18.091805368446025),
('Vackra Nannas park', 'I mitten av Lilla Sköndal finner du Vackra Nannas park, namngiven efter ”Vackra Nanna på Sköndal” ur Per-Anders Fogelströms bok ”Söder om tullen”. ', 59.25896292804213, 18.122339002875123),
('Trudelutten', 'Trudelutten är en stor kvarterspark som ligger i anslutning till Järvafältet i stadsdelen Kista. Parken är ett populärt besöksmål året om.', 59.39958222135566, 17.933154792470873),
('Husby gård', 'Husby gård är en historisk stadsdelspark med bebyggelse från början av 1800-talet. Denna gröna och rofyllda oas erbjuder varierande aktiviteter både på gården och i naturen.', 59.40734898106189, 17.919852025381253),
('Kistagårdsparken', 'Kistagårdsparken är en välbesökt kvarterspark med aktiviteter för både vuxna och barn. Parken är inspirerad av Kista gårds kulturhistoriska miljö med anor från 1700-talet.', 59.40708898339729, 17.940377585195296),
('Nystadsparken', 'Mitt bland de höga husen i den östra delen av Akalla ligger Nystadsparken. En stor kvarterspark med många olika lek- och aktivitetsytor för barn och vuxna.  ', 59.41442096784562, 17.920410761467647),
('Akalla by', 'Akalla by är känd sedan medeltiden. Husen i byn används för fritidsverksamhet med bland annat 4H-gård och är ett populärt utflyktsmål.', 59.41252237341441, 17.91313896615514),
('Teaterparken', '', 59.29887263127664, 17.986970699199297),
('Krausparken', 'Krausparken är ett gångstråk mellan Axelsbergs och Mälarhöjdens tunnelbanestationer. Parken kallas ibland för Spårvagnsparken eftersom gångvägen genom parken är anlagd på den gamla banvallen från spårvagnens tid.', 59.300577393624756, 17.9617508406262),
('Elektraparken', 'Elektraparken är en stadsdelspark i Västberga och utgörs till hälften av parkområde och till hälften av naturmark. I parkens hjärta finns en stor öppen gräsyta som inbjuder till lek och aktivitet.', 59.29525267761185, 18.009092090552507),
('Trekantsparken', 'Trekantsparken ligger i ett centralt läge intill Liljeholmstorget och sjön Trekanten och är en av stadens stadsdelsparker. Parken är en grön oas som erbjuder aktiviteter för olika åldrar.', 59.311304335955576, 18.021676183296623),
('Vinterviken', 'Vinterviksparken ligger i stadsdelen Aspudden och gränsar till Gröndal. Området har fram till 1980-talet dominerats av Alfred Nobels anläggningar. Numera är området en stadsdelspark och grönskande oas.', 59.310279486717796, 17.986098124387883),
('Årstabergsparken', 'Årstabergsparken är en bergspark som sträcker sig genom stadsdelarna Årstaberg och Årstadal mot Årstaskogen. Här finns aktiviteter i alla former för alla åldrar.', 59.305286153343985, 18.026147814924904),
('Stångjärnet', 'Stångjärnet är en central park i korsningen Johannesfredsvägen och Tackjärnsvägen. Parken består av en öppen gräsyta som omges av planterings- och vistelseytor.', 59.345643210277615, 17.971605975627142),
('Klockhusparken', 'Den historiska Klockhusparken ligger längst med Follingbogatan, mittemot före detta Beckomberga sjukhus.', 59.3578519395661, 17.90226003967297),
('Sinnenas trädgård', 'Sinnenas trädgård är en del av det gröna Blackebergsstråket. Parken ligger intill en mindre lekplats, en grusplan med fotbollsmål och natur.', 59.34686149862665, 17.874407031569643),
('Olovslundsparken', 'Olovslundsparken är en central stadsdelspark som omges av småhus med trädgårdar. Parken består av flera gröna och blåa ytor.', 59.33044069912875, 17.940102361009863),
('Målarparken', 'Målarparken är en central liten fickpark intill Medborgarhuset i Alvik. Intill parken ligger Alviks tvärbanestation.', 59.33316651264501, 17.979045646982552),
('Åkeshovs arboretum', 'Åkeshovs arboretum ligger i närheten av Åkeshovs tunnelbanestation, Åkeshofs slott och entrén till Judarskogen. Arboretumet är både en park och en pedagogisk mötesplats.', 59.341978843687926, 17.923339735294505),
('Kerstin Hesselgrens park', 'Kerstin Hesselgrens park ligger i Fruängen och är en grön oas som ger möjlighet till samvaro och ro.', 59.286739946008524, 17.96987372380485),
('Svandammsparken', 'Svandammsparken är en park i Midsommarkransen. Här finns en plaskdamm och en lekplats med en lekattraktion i form av ett spårvagnslok.', 59.30132311082802, 18.00566035116987),
('Aspuddsparken', 'Aspuddsparken är ett större grönområde i stadsdelen Aspudden. Här finns en stor lekplats, en plaskdamm och en parklek med djur. I parken finns också Aspuddens idrottsplats.', 59.308030987266335, 17.994201820991908),
('Starboparken', 'Starboparken är en del av ett grönt stråk som går genom hela Nälsta, parallellt med Nälstastråket. Här finns en stor gräsyta med träd och buskar, en parklek och bollplan.', 59.37367735179623, 17.886604444033512)